import React, { Component } from "react";
import MenuSection from "./MenuSection";
import MenuButton from "./MenuButton";
import { Link, Element, Events, scrollSpy } from "react-scroll";

const menu = [
  {
    // THIS IS A SAMPLE
    menuSectionName: "Appetizers",
    menuSectionItems: [
      {
        id: 1,
        name: "Three Kind Appetizer Combinations",
        price: 32.8,
        isSpicy: true,
      },
      {
        id: 7,
        name: "Lettuce Wrap (Chicken)",
        price: 14.95,
        isSpicy: false,
        menuItemNumber: 20,
      },
    ],
  },
];

const menuSections = ["Appetizers", "Soups", "Beef and Port"];

class Menu extends Component {
  componentDidMount() {
    Events.scrollEvent.register("begin");

    Events.scrollEvent.register("end");

    scrollSpy.update();
  }

  componentWillUnmount() {
    Events.scrollEvent.remove("begin");
    Events.scrollEvent.remove("end");
  }

  render() {
    return (
      <div style={{ paddingBottom: 100 }}>
        <div
          style={{
            paddingLeft: window.outerWidth * 0.02,
            paddingRight: window.outerWidth * 0.02,
            paddingTop: window.outerHeight * 0.02,
            paddingBottom: window.outerHeight * 0.02,
            display: "flex",
            justifyContent: "space-between",
          }}
        >
          {menuSections.map((section) => (
            <Link
              activeClass="active"
              to={section}
              spy={true}
              smooth={true}
              offset={50}
              duration={500}
              onSetActive={this.handleSetActive}
              key={section.menuSectionName}
            >
              <MenuButton
                key={section.menuSectionName}
                buttonName={section}
              ></MenuButton>
            </Link>
          ))}
        </div>
        {menu.map((menuSection) => (
          <Element
            key={menuSection.menuSectionName}
            name={menuSection.menuSectionName}
          >
            <MenuSection
              key={menuSection.menuSectionName}
              addMenuItemToCart={this.props.addMenuItemToCart}
              menuSectionInfo={menuSection}
            ></MenuSection>
          </Element>
        ))}
      </div>
    );
  }
}

export default Menu;
