import React, { Component } from "react";
import MenuStructure from "./MenuStructure";
import Divider from "@material-ui/core/Divider";

class MenuSection extends Component {
  state = {
    sectionName: "",
    sectionItems: [],
  };

  constructor(props) {
    super(props);
    this.state = {
      sectionName: props.menuSectionInfo.menuSectionName,
      sectionItems: props.menuSectionInfo.menuSectionItems,
    };
  }
  render() {
    return (
      <div>
        <MenuSectionTitle
          component={
            <h2 style={{ paddingLeft: "2%" }}>{this.state.sectionName}</h2>
          }
        />
        <Divider
          style={{
            height: "1px",
            width: "96%",
            left: "2%",
            position: "absolute",
            backgroundColor: "black",
          }}
        />
        <MenuStructure
          addMenuItemToCart={this.props.addMenuItemToCart}
          items={this.state.sectionItems}
        />
      </div>
    );
  }
}

const MenuSectionTitle = ({ component }) => {
  return (
    <div style={{ disaply: "flex" }}>
      <div style={{}} />
      {component}
      <div
        style={{
          widht: 0,
          height: 0,
          borderStyle: "solid",
          borderWidth: "50px 0 50px 20px",
          borderColor: "transparent transparent transparent #007bff",
        }}
      />
    </div>
  );
};

export default MenuSection;
