import React, { Component } from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import AddIcon from "@material-ui/icons/Add";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import CardActions from "@material-ui/core/CardActions";

class MenuItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: props.itemInfo.id,
      name: props.itemInfo.name,
      price: props.itemInfo.price,
    };
  }

  render() {
    return (
      <div
        style={{
          width: window.outerWidth * 0.25,
          paddingLeft: window.outerWidth * 0.02,
          paddingRight: window.outerWidth * 0.02,
          paddingTop: window.outerHeight * 0.02,
          paddingBottom: window.outerHeight * 0.02,
        }}
      >
        <Card>
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              height: 100,
            }}
          >
            <CardContent>
              <Typography
                style={{ textOverflow: "ellipsis", whiteSpace: "nowrap" }}
              >
                {this.state.name}
              </Typography>
              <div style={{ height: 25 }} />
              <Typography>{this.state.price}</Typography>
            </CardContent>

            <Button
              style={{ height: 100 }}
              onClick={() =>
                this.props.addMenuItemToCart({
                  id: this.state.id,
                  name: this.state.name,
                  price: this.state.price,
                })
              }
            >
              <AddIcon></AddIcon>
            </Button>
          </div>
        </Card>
      </div>
    );
  }
}

export default MenuItem;
