import React, { Component } from "react";
import TopBar from "../Menu/MenuPage/TopBar";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import PhoneInput from "material-ui-phone-number";
import { Redirect } from "react-router-dom";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";

class OrderConfirmationPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      disabledButton: true,
      firstNameText: "",
      lastNameText: "",
      phoneNumber: "",
      token: props.location.state,
    };
  }

  handleFirstNameEdit = (event) => {
    this.setState({ firstNameText: event.target.value });
  };

  handleLastNameEdit = (event) => {
    this.setState({ lastNameText: event.target.value });
  };

  handlePhoneNumberEdit = (event) => {
    this.setState({ phoneNumber: event.target.value });
  };

  render() {
    if (!this.state.token) {
      return <Redirect to="/404" />;
    }

    return (
      <div>
        <TopBar />
        <Card>
          <div
            style={{ padding: "2%", paddingRight: "10%", paddingLeft: "10%" }}
          >
            <Typography component="h2" style={{ fontSize: 20 }}>
              Please fill in the following to confirm that you will be picking
              up the order.
            </Typography>
            <Typography
              component="h2"
              style={{ fontSize: 20, paddingBottom: "2%" }}
            >
              Your payment will be received upon pick up.
            </Typography>
            <div
              style={{ width: "100%", height: 1, backgroundColor: "black" }}
            ></div>
            <Typography
              component="h2"
              style={{ fontSize: 20, paddingTop: "2%" }}
            >
              First Name
            </Typography>
            <TextField
              fullWidth
              variant="outlined"
              style={{ paddingBottom: 10 }}
              onChange={this.handleFirstNameEdit}
            />
            <Typography component="h2" style={{ fontSize: 20 }}>
              Last Name
            </Typography>
            <TextField
              fullWidth
              variant="outlined"
              style={{ paddingBottom: 10 }}
              onChange={this.handleLastNameEdit}
            />
            <Typography component="h2" style={{ fontSize: 20 }}>
              Phone Number
            </Typography>
            <PhoneInput
              fullWidth
              style={{ height: "40px", paddingTop: "5px" }}
              onChange={this.handlePhoneNumberEdit}
              defaultCountry="ca"
              disableDropdown="true"
              countryCodeEditable="false"
            />
          </div>
          <div
            style={{ padding: "2%", paddingRight: "10%", paddingLeft: "10%" }}
          >
            <Button
              fullWidth
              variant="contained"
              style={{
                padding: "2%",
                fontSize: 20,
                color: "white",
                backgroundColor: "green",
              }}
            >
              Place Order
            </Button>
          </div>
        </Card>
      </div>
    );
  }
}

export default OrderConfirmationPage;
