import React from "react";
import { Switch, Route, Link } from "react-router-dom";
import HomePage from "../Components/HomePage";
import MenuPage from "../Components/Menu/MenuPage/MenuPage";
import CartPage from "../Components/Cart/CartPage";
import OrderConfirmationPage from "../Components/Checkout/OrderConfirmationPage";
import Checkout from "../Components/Checkout/Checkout";

const NoMatchPage = () => {
  return (
    <h1>
      Please return to the main page <Link to="/">here</Link>.
    </h1>
  );
};

const Root = () => {
  return (
    <Switch>
      <Route component={MenuPage} exact path="/:type" />
      <Route component={CartPage} exact path="/:type/cart" />
      <Route
        component={OrderConfirmationPage}
        exact
        path="/:type/cart/order/confirmed"
      />
      <Route
        component={Checkout}
        exact
        path="/:type/cart/order/confirmed/checkout"
      />
      <Route component={HomePage} exact path="/" />
      <Route
        exact
        path="/location"
        component={() =>
          (window.location =
            "https://www.google.com/maps/place/New+King+Do+Seafood+Restaurant/@49.191253,-122.8387086,17z/data=!3m1!4b1!4m5!3m4!1s0x5485d77f3574a219:0x593120f0f17c1726!8m2!3d49.191253!4d-122.8365199")
        }
      />
      <Route component={NoMatchPage} />
    </Switch>
  );
};

export default Root;
