import React, { Component } from "react";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";

class CarBar extends Component {
  state = {
    mouseOverViewCart: false,
    showViewCartDialog: false,
  };

  handleMouseEnterViewCart = () => {
    this.setState({ mouseOverViewCart: true });
  };

  handleMouseLeavingViewCart = () => {
    this.setState({ mouseOverViewCart: false });
  };

  handleShowViewCartDialog = () => {
    this.setState({ showViewCartDialog: true });
  };

  handleOnCloseViewCartDialog = () => {
    this.setState({ showViewCartDialog: false });
  };

  render() {
    return (
      <div
        style={{
          backgroundColor: "white",
          textAlign: "center",
          paddingBottom: "1.5%",
          position: "fixed",
          left: "0",
          bottom: "0",
          height: "4%",
          width: "100%",
          boxShadow: "0px -1px 3px rgba(50, 50, 50, 0.50)",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <div
            style={{
              display: "flex",
              alignItems: "center",
              paddingLeft: "1%",
              width: "15%",
            }}
          >
            <ShoppingCartIcon fontSize="large" />
            <h2 style={{ paddingLeft: "10%" }}>
              {this.props.cartItems.length}
            </h2>
          </div>
          <Button
            style={{
              backgroundColor: "transparent",
              width: "15%",
              color: this.state.mouseOverViewCart ? "grey" : "black",
            }}
            disableRipple={true}
            onMouseEnter={this.handleMouseEnterViewCart}
            onMouseLeave={this.handleMouseLeavingViewCart}
          >
            <h3>View Cart</h3>
            <Dialog
              open={this.state.showViewCartDialog}
              onClose={this.handleOnCloseViewCartDialog}
              aria-labelledby="form-dialog-title"
            >
              <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
              <DialogContent>
                <DialogContentText>
                  To subscribe to this website, please enter your email address
                  here. We will send updates occasionally.
                </DialogContentText>
              </DialogContent>
              <DialogActions>
                <Button color="primary">Cancel</Button>
                <Button color="primary">Subscribe</Button>
              </DialogActions>
            </Dialog>
          </Button>

          <h2
            key={this.props.cartAmount}
            style={{
              fontSize: 20,
              width: "15%",
            }}
          >
            Total Amount: ${this.props.cartAmount}
          </h2>
        </div>
      </div>
    );
  }
}

export default CarBar;
