import React, { Component } from "react";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Card from "@material-ui/core/Card";

class CarBar extends Component {
  state = {
    mouseOverViewCart: false,
    showViewCartDialog: false,
  };

  handleMouseEnterViewCart = () => {
    this.setState({ mouseOverViewCart: true });
  };

  handleMouseLeavingViewCart = () => {
    this.setState({ mouseOverViewCart: false });
  };

  handleShowViewCartDialog = () => {
    this.setState({ showViewCartDialog: true });
  };

  handleOnCloseViewCartDialog = () => {
    this.setState({ showViewCartDialog: false });
  };

  render() {
    return (
      <div
        style={{
          backgroundColor: "white",
          textAlign: "center",
          paddingBottom: "1.5%",
          position: "fixed",
          left: "0",
          bottom: "0",
          height: "4%",
          width: "100%",
          boxShadow: "0px -1px 3px rgba(50, 50, 50, 0.50)",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <Dialog
            open={this.state.showViewCartDialog}
            onClose={this.handleOnCloseViewCartDialog}
          >
            <DialogTitle>Your Cart</DialogTitle>
            <DialogContent></DialogContent>
            <DialogActions>
              <Button color="secondary">Continue Shopping</Button>
              <Button color="primary">Proceed to Checkout</Button>
            </DialogActions>
          </Dialog>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              paddingLeft: "1%",
              width: "15%",
            }}
          >
            <ShoppingCartIcon fontSize="large" />
            <h2 style={{ paddingLeft: "10%" }}>
              {this.props.cartItems.length}
            </h2>
          </div>
          <div
            style={{
              width: "15%",
            }}
          >
            <Button
              style={{
                backgroundColor: "transparent",
                color: this.state.mouseOverViewCart ? "grey" : "black",
              }}
              disableRipple={true}
              onMouseEnter={this.handleMouseEnterViewCart}
              onMouseLeave={this.handleMouseLeavingViewCart}
              onClick={this.handleShowViewCartDialog}
            >
              <h3>View Cart</h3>
            </Button>
          </div>

          <h2
            style={{
              fontSize: 20,
              width: "15%",
              position: "fixed",
            }}
          >
            Total Amount: ${this.props.cartAmount}
          </h2>
        </div>
      </div>
    );
  }
}

export default CarBar;
