import React, { Component } from "react";
import TopBar from "../Menu/MenuPage/TopBar";
import ScrollToTop from "react-scroll-to-top";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Typography from "@material-ui/core/Typography";

class CartPage extends Component {
  constructor(props) {
    super(props);
    let map = new Map();
    const storageCartItems = JSON.parse(sessionStorage.getItem("cartItems"));
    for (const item of storageCartItems) {
      if (!map.has(item.id)) {
        map.set(item.id, [item]);
      } else {
        let listOfItemsWithGivenId = map.get(item.id);
        listOfItemsWithGivenId.push(item);
        map.set(item.id, listOfItemsWithGivenId);
      }
    }
    console.log(map);
    this.state = {
      cartItems: JSON.parse(sessionStorage.getItem("cartItems")),
      itemStack: map,
    };
  }
  render() {
    return (
      <div style={{ backgroundColor: "#FFCCCB" }}>
        <ScrollToTop
          smooth
          style={{
            position: "fixed",
            bottom: "11%",
            left: "47.5%",
            width: "6%",
            height: "6%",
            borderStyle: "solid",
            borderWidth: 1,
            borderColor: "#83858a",
          }}
          component={<h4 style={{ fontSize: 14 }}>Back to Top</h4>}
        />
        <TopBar />
        <div style={{ display: "flex" }}>
          <div
            style={{
              padding: "2%",
              width: "45%",
            }}
          >
            {this.state.cartItems.forEach((value) => {
              return (
                <Card>
                  <CardContent>
                    <Typography variant="h5" component="h2">
                      {value.name}
                    </Typography>
                    <Typography color="textSecondary">{value.price}</Typography>
                    <Typography color="textSecondary">Quantity:</Typography>
                    <TextField
                      variant="outlined"
                      size="small"
                      defaultValue={0}
                    />
                  </CardContent>
                  <CardActions>
                    <Button size="small" color="secondary">
                      Remove Item
                    </Button>
                  </CardActions>
                </Card>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
}

export default CartPage;
