import React, { Component } from "react";
import TopBar from "./TopBar";
import CarBar from "../../Cart/CartBar";
import Menu from "../Menu";
import Button from "@material-ui/core/Button";
import ScrollToTop from "react-scroll-to-top";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Card from "@material-ui/core/Card";

class MenuPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      foodTransportation: props.location.pathname.substring(1),
      cartItems: [],
      cartAmount: "0",
      showViewCartDialog: false,
    };
  }

  addMenuItemToCart = (cartItem) => {
    let currentCarItems = this.state.cartItems;
    let currentCartAmount = parseInt(this.state.cartAmount, 10);
    currentCarItems.push(cartItem);
    currentCartAmount += cartItem.price;
    this.setState({
      cartItems: currentCarItems,
      cartAmount: (Math.round(currentCartAmount * 100) / 100).toFixed(2),
    });
  };

  handleShowViewCartDialog = () => {
    this.setState({ showViewCartDialog: true });
  };

  handleOnCloseViewCartDialog = () => {
    this.setState({ showViewCartDialog: false });
  };

  render() {
    // TODO: if it is anything else, rediect to 404 page
    return (
      <div style={{ backgroundColor: "#FFCCCB" }}>
        <ScrollToTop
          smooth
          style={{
            position: "fixed",
            bottom: "11%",
            left: "47.5%",
            width: "6%",
            height: "6%",
            borderStyle: "solid",
            borderWidth: 1,
            borderColor: "#83858a",
          }}
          component={<h4 style={{ fontSize: 14 }}>Back to Top</h4>}
        />
        <TopBar />
        <Menu addMenuItemToCart={this.addMenuItemToCart} />
        <CarBar
          handleOpenViewCartDialog={this.handleShowViewCartDialog}
          cartAmount={this.state.cartAmount}
          cartItems={this.state.cartItems}
        />
        <Dialog
          open={this.state.showViewCartDialog}
          onClose={this.handleOnCloseViewCartDialog}
        >
          <DialogTitle>Your Cart</DialogTitle>
          <DialogContent></DialogContent>
          <DialogActions>
            <Button color="secondary">Continue Shopping</Button>
            <Button color="primary">Proceed to Checkout</Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}

export default MenuPage;
